require "mini_magick"
require 'miro'
require 'chunky_png'
require 'base64'


Jekyll::Hooks.register :site, :post_read do |site|

  gallery_path = File.join('.', 'assets', 'gallery')
  # puts gallery_path

  unless File.directory?(File.join(gallery_path, 'thumbnail'))
    puts 'Hooks: generating image thumbnails..'

    # FileUtils.remove_dir(File.join(gallery_path, 'thumbnail'))
    Dir.mkdir(File.join(gallery_path, 'thumbnail'))


    Miro.options[:color_count] = 1

    Dir.glob(File.join(gallery_path, '*.{jpg,png,gif}')) do |img|
      puts 'Processing: ' + img

      img_dimensions = MiniMagick::Image.open(img).dimensions
      pp img_dimensions

      img_aspect_ratio = img_dimensions.map { |dimension| dimension / img_dimensions[0].gcd(img_dimensions[1]) }
      pp img_aspect_ratio

      img_dominant_color = Miro::DominantColors.new(img).to_rgb[0]
      pp img_dominant_color

      img_thumbnail = ChunkyPNG::Image.new(img_aspect_ratio[0], img_aspect_ratio[1], ChunkyPNG::Color::TRANSPARENT)

      (0...img_aspect_ratio[0]).each do |row|
        (0...img_aspect_ratio[1]).each do |col|
          img_thumbnail[row, col] = ChunkyPNG::Color.rgb(img_dominant_color[0], img_dominant_color[1], img_dominant_color[2])

        end
      end

      img_thumbnail.save(File.join(gallery_path, 'thumbnail', img.split('/')[-1]), :interlace => true)

      # break
    end


    # image = MiniMagick::Image.open("./assets/gallery/DSC01881W.jpg")
    # pp image.path
    # # pp image.exif
    # image.resize "10x10"
    # image.format image.type
    # image.write "./assets/gallery/thumbnail/output.jpg"


    # img_base64_prefix = "data:image/png;base64,"
    # img_base64 =  Base64.strict_encode64(open("./assets/gallery/thumbnail/output.jpg").read)
    # "data:image/jpg;base64,"
  end
end